export default {
  name: "Statistics",
  props: {
    openFlag: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      echartsStyles: {
        width: '80%',
        height: "500px"
      },
      myChart: null,
      visible: false,
      apiInfo: null
    };
  },
  watch: {
    openFlag: {
      handler: function (newVal, oldVal) {
        if (newVal === true) {
          this.getApiInfo();
        } else {
          this.visible = newVal;
        }
      },
      // 深度观察监听
      deep: true
    }
  },
  mounted() {
    this.visible = this.openFlag;
    // this.getApiInfo()
  },

  methods: {
    afterVisibleChange(val) {
      console.log('visible', val);
    },
    onClose() {
      this.$emit('updateDrawerStatus', false);
    },
    async getApiInfo() {
      await this.$request.getR1Api().then(res => {
        if (res.status !== 200) {
          this.$emit('updateDrawerStatus', false);
        } else {
          this.visible = true;
          this.apiInfo = res.data.data;
          this.echarts();
        }
      });
    },
    echarts() {
      if (this.myChart != null && this.myChart !== "" && this.myChart !== undefined) {
        this.myChart.dispose();
      }
      this.myChart = this.$echarts.init(document.getElementById('main'));
      this.myChart.setOption({
        tooltip: {
          trigger: 'item'
        },
        legend: {
          top: '5%',
          left: 'center',
          // doesn't perfectly work with our tricks, disable it
          selectedMode: false
        },
        series: [{
          name: 'Access From',
          type: 'pie',
          radius: ['40%', '70%'],
          center: ['50%', '70%'],
          // adjust the start angle
          startAngle: 180,
          label: {
            show: true,
            formatter(param) {
              // correct the percentage
              return param.name + ' (' + param.percent * 2 + '%)';
            }
          },
          data: [{
            value: 1048,
            name: 'Search Engine'
          }, {
            value: 735,
            name: 'Direct'
          }, {
            value: 580,
            name: 'Email'
          }, {
            value: 484,
            name: 'Union Ads'
          }, {
            value: 300,
            name: 'Video Ads'
          }, {
            // make an record to fill the bottom 50%
            value: 1048 + 735 + 580 + 484 + 300,
            itemStyle: {
              // stop the chart from rendering this piece
              color: 'none',
              decal: {
                symbol: 'none'
              }
            },
            label: {
              show: false
            }
          }]
        }]
      });

      // 让图表跟随屏幕自动的去适应
      window.addEventListener('resize', () => {
        this.myChart.resize();
      });
    }
  }
};
