import {service} from '@/api/index'


export const switchApi = (type, flag) => {
    return service
        .request({
            url: '/quickSwitch',
            method: 'put',
            data: {
                data:{
                    type: type,
                    flag: flag,
                }
            }
        })
        .then(res=> res)
}

export const switchStatus = () => {
    return service
        .request({
            url: '/switchStatus',
            method: 'get',
        })
        .then(res=> res)
}

export const getR1Api = () => {
    return service
        .request({
            url: '/functional/getApiInfo',
            method: 'get',
        })
        .then(res=> res)
}
