import {service} from '@/api/index'


export const switchApi = (params, type, flag) => {
    return service
        .request({
            url: params,
            method: 'put',
            data: {
                data:{
                    type: type,
                    flag: flag,
                }
            }
        })
        .then(res => res)
}
