import "core-js/modules/es.array.push.js";
export default {
  name: "RunNewman",
  props: {
    openFlag: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      loading: false,
      visible: false,
      formLayout: 'horizontal',
      form: {
        name: '',
        description: ''
      },
      rules: {
        name: [{
          required: true,
          message: '请输入操作名称',
          trigger: 'blur'
        }, {
          min: 5,
          max: 50,
          message: '长度必须在 5 ～ 50',
          trigger: 'blur'
        }]
      }
    };
  },
  watch: {
    openFlag: {
      handler: function (newVal, oldVal) {
        console.log(newVal);
        this.visible = newVal;
      },
      // 深度观察监听
      deep: true
    }
  },
  mounted() {
    this.visible = this.openFlag;
  },
  methods: {
    handleOk(e) {
      const h = this.$createElement;
      this.loading = true;
      this.$refs.ruleForm.validate(valid => {
        if (valid) {
          this.$confirm({
            title: '在创建测试任务前需要和您确认',
            content: h => h("div", {
              "style": "color:red;"
            }, ["\u60A8\u5F53\u524D\u6570\u636E\u5E93\u662F\u5426\u7B26\u5408\u6D4B\u8BD5\u73AF\u5883?"]),
            okText: '是的，创建任务',
            cancelText: '去导入数据库',
            onOk: () => {
              setTimeout(() => {
                var param = {
                  data: this.form
                };
                this.$request.postNewmanTasks(param).then(res => {
                  if (res.status === 200) {
                    this.$emit('newmanTask', res.data.data);
                    this.$message.success("创建测试任务成功，请到任务列表查看");
                  } else {
                    this.$message.error("创建测试失败");
                  }
                });
                this.$emit('updateModelStatus', false);
                this.loading = false;
                this.initFormData();
              }, 1);
            },
            onCancel: () => {
              this.$router.push("/backup");
            }
          });
        } else {
          this.loading = false;
          return false;
        }
      });
    },
    handleCancel(e) {
      this.$emit('updateModelStatus', false);
    },
    afterClose(e) {
      this.$emit('updateModelStatus', false);
    },
    handleChange(info) {
      const status = info.file.status;
      if (status !== 'uploading') {
        console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        this.$message.success(`${info.file.name} file uploaded successfully.`);
      } else if (status === 'error') {
        this.$message.error(`${info.file.name} file upload failed.`);
      }
    },
    initFormData() {
      this.$refs.ruleForm.resetFields();
    }
  }
};
