    getPanelChange(moment) {
      var startDate = this.getStartDate(moment);
      var endDate = this.getEndDate(moment);
      this.getLogs(moment);
    },
    getStartDate(moment) {
      return moment.startOf('month').subtract(1, 'month').add(1, 'day').format('YYYY-MM-DD');
    },
    getEndDate(moment) {
      return moment.endOf('month').add(1, 'day').format('YYYY-MM-DD');
    },
    getLogs(moment) {
      var startDate = this.getStartDate(moment);
      var endDate = this.getEndDate(moment);
      var param = "?start=" + startDate + "&target=" + endDate;
      this.$request.getLog(param).then(res => {
        console.log(res);
      });
    },
    onSelect(value) {
      const date = moment(value);
      const formattedDate = date.format('YYYY-MM-DD');
      var param = "?created_at=" + formattedDate;
      this.$request.getLog(param).then(res => {
        console.log(res);
      });
    }
  }
};
