import vueJsonEditor from 'vue-json-editor';
export default {
  name: "Json",
  data() {
    return {
      hasJsonFlag: true,
      // json是否验证通过
      jsonText: {}
    };
  },
  components: {
    vueJsonEditor
  },
  created() {},
  methods: {
    onJsonChange(value) {
      // 实时保存
      this.onJsonSave(value);
    },
    onJsonSave(value) {
      this.hasJsonFlag = true;
      this.$emit('updateJsonData', {
        value: value,
        hasJsonFlag: this.hasJsonFlag
      });
    },
    onError(value) {
      this.hasJsonFlag = false;
      this.$emit('updateJsonData', {
        value: value,
        hasJsonFlag: this.hasJsonFlag
      });
    }
  }
};
