export default {
  name: "NewmanTask",
  props: {
    openFlag: {
      type: Boolean,
      required: true
    },
    taskName: {
      type: String,
      required: true
    },
    taskLog: {
      type: Array,
      required: true
    }
  },
  data() {
    return {
      loading: false,
      visible: false
    };
  },
  computed: {
    // 对taskLog数据中status属性判断，用于显示当前任务节点的icon颜色
    computedColor() {
      return function (item) {
        if (item.flag && item.status === "成功") {
          return 'green';
        }
        if (!item.flag && item.status === "") {
          return 'gray';
        }
        return 'blue';
      };
    }
  },
  watch: {
    openFlag: {
      handler: function (newVal, oldVal) {
        // 父组件给的openFlag值发变化 拷贝一份
        this.visible = newVal;
      },
      // 深度观察监听
      deep: true
    }
  },
  mounted() {
    this.visible = this.openFlag;
  },
  methods: {
    afterClose(e) {
      // 向父组件发送updateModelStatus事件，表示modal关闭
      this.$emit('updateModelStatus', false);
    }
  }
};
