export default {
  props: {
    codeText: {
      type: String,
      required: true
    },
    languageClass: {
      type: String,
      required: true
    }
  },
  watch: {
    codeText: {
      handler: function (newVal, oldVal) {
        this.codeText = newVal;
        this.updateSourceCode(newVal);
      },
      // 深度观察监听
      deep: true
    }
  },
  name: "Code",
  data() {
    return {};
  },
  methods: {
    updateSourceCode(code) {
      this.codeText = code;
    }
  }
};
